import React from 'react';
import './App.css';
import TextField from "@material-ui/core/TextField";



class GetStarted extends React.Component {

  state = {
    current_ptp : '',
    ptp_list : []
  }

  mapPtps() {
    return this.state.ptp_list.map((ptp) => {
      <li> ptp </li>
    })
  }

  updateField(field, newValue) {
    console.log( newValue )
    this.setState({
      ...this.state,
      [field]: newValue
    });
  }

  addToPtpList = ( ptp ) => {
    console.log( "about to add " + ptp )
    let list = this.state.ptp_list
    console.log( "old list")
    console.log( list )
    let new_list = list.concat( ptp )
    console.log( "new list")
    console.log( new_list )
    this.setState({ current_ptp: '', ptp_list : new_list})
  }

  render(){
    return (
      <div className="App">
        <h1> GETTING STARTED </h1>
        <br/>
        <TextField
          id="outlined-helperText"
          label=""
          placeholder="Name"
          helperText="Nothing"
          variant="outlined"
          onChange={e =>
            this.updateField("current_ptp", e.target.value)
          }
          onKeyPress={e => {
            if (e.key === 'Enter') {
              this.addToPtpList( this.state.current_ptp)
            }
          }}
        />
        <div>ptps</div>
        <div> {this.mapPtps()} </div>
      </div>
    );
  }

}

export default GetStarted;
