import React from 'react';
import './App.css';
import jquery from 'jquery'


class PlayGame extends React.Component {

  state = {
    ptp_list : this.props.location.state.ptp_list,

    // %
    questions : [ 
      { text : 'question 1 is for {0}', ptps_needed : 2 },
      { text : 'question 2', ptps_needed : 3 }
    ]
  }

  formatString = ( str, args ) => { 
    let returnStr = ''
    for ( let i = 0; i < args.length; i++) {
      console.log( "in for with " + args[i])
      returnStr = str.replace( '{}', args[i] )
    }

    console.log( "returning : " + returnStr )
    return returnStr
  }

  displayRandomQuestion = () => {
    let q = this.state.questions[0]

    let filled_q  = this.formatString( q.text , ['Bob'])
    console.log( "filled q: ")
    console.log( filled_q )
    return ( <div> {filled_q} </div> )
  }

  render(){
    return (
      <div>
        <div> Let's play the game! </div>
        <div> { this.state.ptp_list.length }</div>
    <div> { this.displayRandomQuestion() }</div>

      </div>
    );
  }

}

export default PlayGame;
