import React from 'react';
import './App.css';
import Button from '@material-ui/core/Button';
import GameHeader from './GameHeader.js'



class PlayGame extends React.Component {

  state = {
    ptp_list : this.props.location.state.ptp_list,

    displayed_q : '',

    // %
    questions : [ 
      { text : '{} and {} need to do a funny dance.', ptps_needed : 2 },
      { text : 'If your name is {}, drink.', ptps_needed : 1 },
      { text : '{}, say one reason why you like {}. And then give out 3 drinks.', ptps_needed : 2 },
      { text : 'Name a celebrity you think is overrated. If you agree, drink. {} starts.', ptps_needed : 1 },
      { text : '{} choose a number between 1 and 10. {} and {} both guess what that number is. The loser drinks 4 times.}', ptps_needed : 4 }
    ]
  }

  componentDidMount = () => {
    this.displayRandomQuestion()
  }

  formatString = ( str, args ) => { 
    let return_str = str
    for ( let i = 0; i < args.length; i++ ){
      return_str = return_str.replace( '{}', args[i] )
    }
    return return_str
  }

  chooseRandomPtps = ( n ) => {
    let ptps = this.state.ptp_list

    let random_nums = []
    while (random_nums.length < n) {
      var item = ptps[Math.floor(Math.random() * ptps.length)];
      if ( !random_nums.includes( item ) ){
        random_nums.push( item )
      }
    }

    return random_nums
  }

  chooseRandomQuestion = () => {
    let qs = this.state.questions
    let item = qs[Math.floor(Math.random() * qs.length)];
    return item
  }

  displayRandomQuestion = () => {
    let random_q = this.chooseRandomQuestion()

    if (random_q.ptps_needed > this.state.ptp_list.length) {
      // CHOOSE A DIFFERENT RANDOM Q
    }
    let random_ptps = this.chooseRandomPtps( random_q.ptps_needed )
    let filled_q  = this.formatString( random_q.text , random_ptps )

    console.log( "filled q: ")
    console.log( filled_q )
    this.setState( {displayed_q : filled_q} )
  }

  completeQuestion = () => {
    this.displayRandomQuestion()
  }

  render(){
    return (
      <div className="App" >
        <GameHeader/>
        <div className="Question"> { this.state.displayed_q }</div>
        <div className="Done-Button"> 
          <Button onClick={() => this.completeQuestion()} variant="contained" color="secondary" > Done </Button>
        </div>
      </div>
    );
  }

}

export default PlayGame;
