    this.setState({
      ...this.state,
      [field]: newValue
    });
  }

  addToPtpList = ( ptp ) => {
    console.log( "about to add " + ptp )
  }

  render(){
    return (
      <div className="App">
        <h1> GETTING STARTED </h1>
        <br/>
        <TextField
          id="outlined-helperText"
          label=""
          placeholder="Name"
          helperText="Nothing"
          variant="outlined"
          onChange={e =>
            this.updateField("current_ptp", e.target.value)
          }
          onKeyPress={e => {
            if (e.key === 'Enter') {
              this.addToPtpList( this.state.current_ptp)
            }
          }}
        />
        <div>ptps</div>
        <div> {this.mapPtps()} </div>
      </div>
    );
  }

}

export default GetStarted;
