import React from 'react';
import './App.css';
import TextField from "@material-ui/core/TextField";



class GetStarted extends React.Component {

  state = {
    current_ptp : '',
    ptp_list : null
  }

  mapPtps() {
    if (this.state.ptp_list == null) {
      console.log( "NOTHING ADDED YET")
    } else {
      console.log( "LEN: " + this.state.ptp_list.length )

      return this.state.ptp_list.map( 
        (item) => {
          return  <p> {item} </p>
        }
      )
    }
  }

  updateField(field, newValue) {
    console.log( newValue )
    this.setState({
      ...this.state,
      [field]: newValue
    });
  }

  addToPtpList = () => {
    let list = null 
    if (this.state.ptp_list == null) {
      console.log( "NOTHING ADDED YET")
      list = []
    } else {
      list = this.state.ptp_list
    }
    console.log( "add " + this.state.current_ptp )
    let new_list = list.push( this.state.current_ptp )
    this.setState( {ptp_list : new_list, current_ptp : "" } )
  }

  render(){
    return (
      <div className="App">
        <h1> GETTING STARTED </h1>
        <br/>
        <TextField
          id="outlined-helperText"
          label=""
          placeholder="Name"
          helperText="Nothing"
          variant="outlined"
          onChange={e =>
            this.updateField("current_ptp", e.target.value)
          }
          onKeyPress={e => {
            if (e.key === 'Enter') {
              this.addToPtpList()
            }
          }}
        />
        <div>ptps</div>
        <div> {this.mapPtps()} </div>
      </div>
    );
  }

}

export default GetStarted;
