import React from 'react';
import './App.css';
import TextField from "@material-ui/core/TextField";
import Button from '@material-ui/core/Button';
import Input from "@material-ui/core/Input";



class GetStarted extends React.Component {

  state = {
    current_ptp : '',
    ptp_list : [],
    message : '', ///TODO ADD THERE MUST BE > 1 PTP
  }

  mapPtps() {
    let colors = [ 'red', 'blue', 'green', 'orange', 'yellow', 'pink' ]

    return this.state.ptp_list.map((ptp) => {
      var bubble_color = colors.shift()
      return <div>
          <button onClick={() => this.onDeletePtp(ptp)}> delete </button>
          <div className="Ptp-Bubble" style={{ backgroundColor: bubble_color }}> {ptp} </div>
        </div>
    })
  }


  onDeletePtp = (ptp) => {
    console.log( "need to delete: " + ptp )
    var list = this.state.ptp_list
    for ( let i = 0; i < list.length; i++ ){
      if( list[i] == ptp ){
        list.splice(list.indexOf(ptp), 1 )
        console.log( list )
      }
    }

    this.setState({ ptp_list : list })
  }


  updateField(field, newValue) {
    console.log( newValue )
    this.setState({
      ...this.state,
      [field]: newValue
    });
  }

  addToPtpList = ( ptp ) => {
    let list = this.state.ptp_list
    if( list.length == 8 ) {
      this.setState({ message : "Sorry! There is a maximum of 8 participants." })
    } else {
      let new_list = list.concat( ptp )
      this.setState({ current_ptp: '', ptp_list : new_list})
    }
  }

  onLetsPlay = () => {
    this.props.history.push( {
      pathname: "/play",
      state: {
        ptp_list : this.state.ptp_list
      }
    } )
  }

  clearTextField = (event) => {
    event.target.value = ''
  }


  render(){
    return (
      <div className="App">
        <h1> GETTING STARTED </h1>
        <p>Enter the name of a player</p>
        <br/>
        <div className="TextField">
          <Input
            id="outlined-helperText"
            placeholder="Name"
            fullWidth="true"
            onChange={e =>
              this.updateField("current_ptp", e.target.value)
            }
            onKeyPress={e => {
              if (e.key === 'Enter') {
                this.addToPtpList(this.state.current_ptp)
                this.clearTextField(e)
              }
            }}
          />
        </div>
        <br/>
        <div className="Bubble-Region"> 
          {this.mapPtps()} 
        </div>
        <div> {this.state.message}</div>
        <div className="Footer"> 
          <Button onClick={() => this.onLetsPlay()} color="primary">Let's Play!</Button>
        </div>


      </div>
    );
  }

}

export default GetStarted;
