import React from 'react';
import './App.css';
import TextField from "@material-ui/core/TextField";



class GetStarted extends React.Component {

  state = {
    current_ptp : '',
    ptp_list : []
  }

  updateField(field, newValue) {
    console.log( newValue )
    this.setState({
      ...this.state,
      [field]: newValue
    });
  }

  addToPtpList = ( ptp_name ) => {
    let list = this.state.ptp_list
    let new_list = list.push( this.state.current_ptp )
    this.setState( {ptp_list : new_list} )
  }

  render(){
    return (
      <div className="App">
        <h1> GETTING STARTED </h1>
        <br/>
        <TextField
          id="outlined-helperText"
          label=""
          placeholder="Name"
          helperText="Nothing"
          variant="outlined"
          onChange={e =>
            this.updateField("current_ptp", e.target.value)
          }
          onKeyPress={e => {
            if (e.key === 'Enter') {
              this.addToPtpList()
            }
          }}
        />
        <div> Len: { this.state.ptp_list.length }</div>
      </div>
    );
  }

}

export default GetStarted;
